async function createOffscreen(){await chrome.offscreen.hasDocument()||await chrome.offscreen.createDocument({url:"offscreen.html",reasons:["DOM_PARSER"],justification:"testing"})}console.log("Hello World"),chrome.runtime.onMessage.addListener((async e=>{switch(e.type){case"play":await createOffscreen(),await chrome.runtime.sendMessage({type:"play",play:e.play,offscreen:!0});break;case"pause":await createOffscreen(),await chrome.runtime.sendMessage({type:"pause",offscreen:!0})}}));